import React from 'react'
import 'p5'
import p5 from "p5";
import Data from './output'

class Galaxy extends React.Component {
  constructor(props) {
    super(props);
    this.sketchRef = React.createRef()
    this.state = {
      sketch: undefined,
    }
    console.log("WHAAAT")
    console.log(Data)
  }
  handleResize = (e) => {
    this.state.sketch.resizeCanvas(window.innerWidth, window.innerHeight, false)
   };

  galaxy = (sketch) => {
    this.state.sketch = sketch;
    sketch.setup = () => {
      sketch.createCanvas(window.innerWidth, window.innerHeight);
      sketch.background(0);
      sketch.fullscreen();
      sketch.stroke('white')
      sketch.strokeWeight(2);
    }

    sketch.draw = () => {
        sketch.background(0)
      let arr = Data[parseInt(Date.now() / 100) % 313]
      for (let i=0; i < arr.length; i++) {
          stroke(255 * arr[i][1])
          sketch.point(arr[i][0] * window.innerWidth, arr[i][2] * window.innerHeight)
      }
    }
  }

  componentDidMount() {
    this.sketch_export =  new p5(this.galaxy, this.sketchRef.current);
    window.addEventListener("resize", this.handleResize);
  }
  render() {
    return <div style={{position:this.props.position, zIndex: 0, top: 0, marginBottom: '-20px'}} ref={this.sketchRef}></div>;
  }

}

export default Galaxy;
