import React from 'react'
import { Transition } from 'react-spring/renderprops.cjs'

class GalleryItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            expand: false,
            item:<div id={this.props.title} className="jumbotron gallery-piece">
                    <h1>{this.props.title}</h1>
                    <p>{this.props.description}</p>
                </div>
        }
    }

    render() {
        return (
            <Transition
                items = {this.state.expand}
                from={{position: 'relative', width: '50%'}}
                enter={{ position: 'fixed', width: '100%'}}
                leave = {{position: 'relative', width: '50%'}}
                >
                    {expand => expand && (props => <div style={props}>{this.state.item}</div>)}

            </Transition>
        )
    }
}

export default GalleryItem;
