import React from 'react'
import { Trail } from 'react-spring/renderprops.cjs'

class Gallery extends React.PureComponent {
    constructor(props) {
      super(props);
    }
    state = { toggle: true, items: ['item1', 'item2', 'item3', 'item4', 'item5'] }
    toggle = () => this.setState(state => ({ toggle: !state.toggle }))
    render() {
      const { toggle, items } = this.state
      return (
        <Trail items={this.props.galleryElements} from={{transform: 'translate3d(0,400px,0)'}} to={{transform: 'translate3d(0,0px,0)'}}>
          {item => props => <div style={props}>{item}</div>}
        </Trail>
      )
    }
  }

export default Gallery;
