import React from 'react'
import { Trail } from 'react-spring/renderprops.cjs'

class Gallery extends React.PureComponent {
    constructor(props) {
      super(props);
      this.state = {
        keys: {}
      }

      for (var i=0; i < this.props.galleryElements.length; i++) {
        this.state.keys[this.props.galleryElements[i]] = i
      }
    }
    state = { toggle: true, items: ['item1', 'item2', 'item3', 'item4', 'item5'] }
    toggle = () => this.setState(state => ({ toggle: !state.toggle }))
    render() {
      const { toggle, items } = this.state
      return (
        <Trail items={this.props.galleryElements} keys={item=> this.state.keys[item]}>
          {this.props.galleryElements}
        </Trail>
      )
    }
  }

export default Gallery;
