import React from 'react'
import { Transition } from 'react-spring/renderprops.cjs'

class GalleryItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            expand: false,
        }
    }

    handleClick = () => {
        this.state.expand = !this.state.expand
    }

    render() {
        return (
            <Transition
                items = {this.state.expand}
                from={{position: 'relative', width: '50%'}}
                enter={{ position: 'fixed', width: '100%', margin: '0'}}
                leave = {{position: 'relative', width: '50%'}}
                >
                    {expand => expand ? (props => 
                        <div onClick={() => handleClick()} id={this.props.title} style={props} className="jumbotron gallery-piece">
                            <h1>{this.props.title}</h1>
                            <p>{this.props.description}</p>
                        </div>
                    )
                    :(props => 
                        <div onClick={() => handleClick()} id={this.props.title} style={{position: 'relative', width: '50%'}} className="jumbotron gallery-piece">
                            <h1>{this.props.title}</h1>
                            <p>{this.props.description}</p>
                        </div>
                    )
                }

            </Transition>
        )
    }
}

export default GalleryItem;
