import React from 'react'
import { Transition, animated } from 'react-spring/renderprops.cjs'

class GalleryItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            expand: false,
        }
    }

    handleClick = () => {
        this.setState(state => ({
            expand: !this.state.expand
        }));
    }

    render() {
        return (
            <div>
                <div onClick={() => this.handleClick()} id={this.props.title} style={{position: 'relative', width: '50%'}} className="jumbotron gallery-piece">
                    <h1>{this.props.title}</h1>
                    <p>{this.props.description}</p>
                </div>
                <Transition
                    items = {this.state.expand}
                    from={{opacity: 0,}}
                    enter={{ opacity: 1, position: 'fixed', width: '100%', margin: '0'}}
                    leave = {{opacity: 0, width: '0px'}}
                    >
                        {expand => expand && (props => 
                            <animated.div onClick={() => this.handleClick()} id={this.props.title} style={props} className="jumbotron gallery-piece">
                                <h1>{this.props.title}</h1>
                                <p>{this.props.description}</p>
                            </animated.div>
                        )
                    }

                </Transition>
            </div>
        )
    }
}

export default GalleryItem;
