import React from 'react'
import ImageGallery from 'react-image-gallery'
import { Transition } from 'react-spring/renderprops.cjs'
import Gallery from './layout/portfolio-gallery'

const avciso_images = [
    {
        original: "api/images/avciso-1.png",
        description: `AvCISO`
    },
    {
        original: "api/images/avciso-2.png",
        description: `AvCISO`
    },
    {
        original: "api/images/avciso-3.png",
        description: `AvCISO`
    }
    ]

const public_ballot_images = [
    {
        original: "api/images/publicballot-1.png",
        description: `PublicBallot.org`
    },
    {
        original: "api/images/publicballot-2.png",
        description: `PublicBallot.org`
    },
    {
        original: "api/images/publicballot-3.png",
        description: `PublicBallot.org`
    }
    ]

const pages = [
    style => (
      <animated.div style={{ ...style, background: '#b3FFBD' }}>
              <center>
                <div style={{width: '75%'}}>
                  <ImageGallery items={avciso_images} showPlayButton={false} showThumbnails={false}/>
                </div>
              </center>
              <div style={{margin: '10px'}}></div>
              <center>
                <div style={{width: '75%'}}>
                  <ImageGallery items={public_ballot_images} showPlayButton={false} showThumbnails={false}/>
                </div>
              </center>
      </animated.div>
    ),
    style => (
      <animated.div style={{ ...style, background: '#B2DBBF' }}>
        <Gallery />
      </animated.div>
    ),
  ]


class Main extends React.PureComponent {
    state = { index: 0 }
    toggle = e =>
        this.setState(state => ({
        index: state.index === 2 ? 0 : state.index + 1,
    }))

    render() {
        return (
            <div className="main" onClick={this.toggle}>
            <Transition
              native
              reset
              unique
              items={this.state.index}
              from={{ opacity: 0, transform: 'translate3d(100%,0,0)' }}
              enter={{ opacity: 1, transform: 'translate3d(0%,0,0)' }}
              leave={{ opacity: 0, transform: 'translate3d(-50%,0,0)' }}>
              {index => pages[index]}
            </Transition>
          </div>
        )
    }
}

export default Main;
