import React from 'react'
import { Spring, animated } from 'react-spring/renderprops.cjs'
import PortfolioElement from './portfolio-element'

const avciso_images = [
    {
      original: "api/images/avciso-1.png",
      description: `AvCISO`
    },
    {
      original: "api/images/avciso-2.png",
      description: `AvCISO`
    },
    {
      original: "api/images/avciso-3.png",
      description: `AvCISO`
    }
  ]

class PortfolioProjects extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            theComeUp: 0
        }
    }

    open_window = () => {

        this.setState({
            theComeUp: 1
        })
    }

    close_window = () => {
        this.setState({
            theComeUp: 0
        })
    }

    render() {
        return (
            <Spring
                toggle={this.state.theComeUp}
                from={{
                    opacity: 0, 
                    transform: 'translate3d(0,40px,0)'
                }}
                to={{ 
                    opacity: this.state.theComeUp ? 1 : 0, 
                    transform: this.state.theComeUp ? 'translate3d(0,40px,0)' : 'translate3d(0,0,0)'
                }}>
                {({ opacity, transform}) => 
                        <animated.div id='fullscreen' style={{opacity, transform, zIndex: 3, position: 'absolute', height: '80%', width: '100%', backgroundColor: 'grey', zIndex: '10'}}>
                            <PortfolioElement images={avciso_images} />
                        </animated.div>
                }
            </Spring>
        )
    }
}

export default PortfolioProjects;
