import React from 'react'
import { config, Trail } from 'react-spring/renderprops.cjs'

class Gallery extends React.PureComponent {
    constructor(props) {
      super(props);
      this.state = {
        offset: 0,
        i: 0
      }
      console.log(this.props.galleryElements);
/*      this.props.galleryElements.forEach(item => {
        item.style.marginTop = '100px';
      })*/
    }

    componentDidMount() {
      let offset = this.state.offset
      let elements = document.getElementById('elements').children
      console.log(elements)
      console.log(elements.length)
      for (let j = 0; j < elements.length; j++) {
        let element = elements[j]
        console.log(element)
        element.style.top = offset + 10;
        offset += element.offsetHeight + 10;
      }
    }

    render() {
      return (
        <div id='elements'>
          <Trail items={this.props.galleryElements} from={{transform: 'translate3d(0,400px,0)'}} to={{transform: 'translate3d(0,0px,0)'}}>
            {item => props => this.state.i++ && 
            (
              <div style={{...props, }}>{item}</div>
            )}
          </Trail>
        </div>
      )
    }
  }

export default Gallery;
