import Head from 'next/head'
import styles from '../styles/Home.module.css'
import ScriptTag from 'react-script-tag'
import dynamic from 'next/dynamic'
import Topnav from '../components/layout/topnav'
import Main from '../components/main'
import React from 'react'

const Circles = dynamic(
  () => import('../components/visuals/circles'),
  {
    ssr: false
  }
)

class Home extends React.Component {
  componentDidMount() {
    let main_text = document.getElementById('main-text')
    let nav_height = document.getElementById('navcol-1').offsetHeight + 50
    main_text.style.width = `${window.innerWidth}px`
    main_text.style.height = `${window.innerHeight - nav_height}px`
    main_text.children[0].style.top = `${(window.innerHeight - nav_height) / 2}px`
    window.addEventListener('resize', function() {
      main_text.children[0].style.top = `${(window.innerHeight - nav_height) / 2}px`
      main_text.style.width = `${window.innerWidth}px`
      main_text.style.height = `${window.innerHeight - nav_height}px`
    })
  }

  render() {
    return (
    <div>
      <Topnav currentPage='Home'></Topnav>
      <section style={{textAlign: "center"}}>
        <Circles position='fixed' HD='true' />
        <div id='main-text' style={{position: 'absolute', textAlign: 'center', width: '100%', height: '100%'}}>
          <div style={{ top: '0px', bottom: '0px', position: 'absolute', fontWeight: 'bolder'}}>
            <h1>Hi, I'm Caleb</h1>
            <h2>Welcome to my dojo</h2>
          </div>
        </div>
      </section>
    </div>
  )}
}

export default Home;
