import React from 'react'
import { Spring, animated, config } from 'react-spring/renderprops.cjs'

class GalleryItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            expand: false,
            width: 0,
            height: 0,
            render: false
        }
    }

    handleClick = () => {
        console.log("BITCH")
        this.setState(state => ({
            expand: !state.expand
        }));
    }

    componentDidMount() {
        this.state.width = window.innerWidth;
        this.state.height = window.innerHeight;
        this.state.render = true
    }

    render() {
        return (
            <div>

                <Spring
                    toggle={this.state.expand}
                    config = {config.slow}
                    from={{opacity: 0, width: 'inherit'}}
                    to={{ 
                        opacity: this.state.expand ? 1 : 0, 
                        position: this.state.expand ? 'relative' : 'fixed', 
                        width: this.state.expand ? this.state.width : this.state.width, 
                        margin: '0', 
                        height: this.state.expand ? this.state.height : 'inherit'
                    }}>
                        {({ opacity, position, margin, height, width }) => 
                            <animated.div onClick={() => this.handleClick()} id={this.props.title} 
                                style={{
                                    width,
                                    /*position,*/
                                    /*margin*/
                                }} className="jumbotron gallery-piece">
                                <h1>{this.props.title}</h1>
                                <p>{this.props.description}</p>
                            </animated.div>
                    }

                </Spring>
            </div>
        )
    }
}

export default GalleryItem;
