import React from 'react'
import { Spring, animated, config } from 'react-spring/renderprops.cjs'

class GalleryItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            expand: false,
            width: 0,
            height: 0
        }
    }

    handleClick = () => {
        console.log("BITCH")
        this.setState(state => ({
            expand: !state.expand
        }));
    }

    componentDidMount() {
        this.state.width = window.innerWidth;
        this.state.height = window.innerHeight;
    }

    render() {
        return (
            <div>

                <Spring
                    toggle={this.state.expand}
                    config = {config.slow}
                    from={{opacity: 0}}
                    to={{ 
                        opacity: this.state.expand ? 1 : 0, 
                        position: 'fixed', width: '100%', 
                        margin: '0', 
                        height: this.state.expand ? this.state.height : 'inherit'
                    }}>
                        {({ opacity, position, margin, height }) => 
                            <animated.div onClick={() => this.handleClick()} id={this.props.title} 
                                style={
                                    opacity
                                } className="jumbotron gallery-piece">
                                <h1>{this.props.title}</h1>
                                <p>{this.props.description}</p>
                            </animated.div>
                    }

                </Spring>
            </div>
        )
    }
}

export default GalleryItem;
