import React from 'react'
import { Spring, animated } from 'react-spring/renderprops.cjs'

class GalleryItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            expand: false,
        }
    }

    handleClick = () => {
        this.setState(state => ({
            expand: !this.state.expand
        }));
    }

    render() {
        return (
            <div>

                <Spring
                    toggle={this.state.expand}
                    from={{opacity: 0, width: '0px'}}
                    to={{ opacity: 1, position: 'fixed', width: '1020%', margin: '0'}}
                    >
                        {props => 
                            <animated.div onClick={() => this.handleClick()} id={this.props.title} style={props} className="jumbotron gallery-piece">
                                <h1>{this.props.title}</h1>
                                <p>{this.props.description}</p>
                            </animated.div>
                    }

                </Spring>
            </div>
        )
    }
}

export default GalleryItem;
