import React from 'react'
import { config, Trail } from 'react-spring/renderprops.cjs'

class Gallery extends React.PureComponent {
    constructor(props) {
      super(props);
      let elements = [];
      let refs = [];
      for (let i=0; i < this.props.galleryElements.length; i++) {
        elements.push(this.props.galleryElements[i])
        elements[i].ref = React.createRef();
      }
      this.state = {
        elements: elements
      }
    }
    componentDidMount() {
      for (let i=0; i < this.props.galleryElements.length; i++) {
        console.log(this.props.galleryElements[i])
      }
    }
    render() {
      return (
        <div id='elements'>
          {this.state.elements}
        </div>
      )
    }
  }

export default Gallery;
